package lec02;

public class pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				int n=5;
				int row=1;
				while(row<=n) {
					int i=5;
					while(i>=row) {
					System.out.print("* ");
					i--;
					}
					row++;
					System.out.println();
				

		}
	}

}
